var devices = {};

msg.payload.forEach(d =>
{
    devices[d.serial] = d;
})

flow.set('devicesBySerial', devices)
msg.payload = devices;
return msg;

var devices = flow.get('devicesBySerial');

var srcSerial = msg.serial;
var dstSerial = msg.payload;

if(devices[srcSerial].model !== devices[dstSerial].model){
    node.status({fill:"red",shape:"ring",text:"models do not match"});
}else{
    node.status({fill:"green",shape:"ring",text:"models match"});
    return msg;
}

msg.payload = msg.payload.status

if(msg.payload.completed){
    node.status({fill:"green",shape:"ring",text:"completed"});
}else{
    node.status({fill:"yellow",shape:"ring",text:"in progress"});
    return msg;
}

if(msg.payload.error){
    node.status({fill:"red",shape:"ring",text:"error"});
}
return msg;
