    vertical-align: middle;
}

$(document).ready(function(e) {

    $("form[ajax=true]").submit(function(e) {

        e.preventDefault();

        var form_data = $(this).serialize();
        var form_url = $(this).attr("action");
        var form_method = $(this).attr("method").toUpperCase();

        $("#loadingimg").show();

        $.ajax({
            url: form_url, 
            type: form_method,      
            data: form_data,     
            cache: false,
            success: function(returnhtml){                          
                $("#loadingimg").hide();
                console.log("result ", returnhtml);
                $("#result").html(JSON.stringify(returnhtml, null, 2)); 
            },
            error: function(e){
                $("#loadingimg").hide();
                console.log("error ", e);
                $("#result").html(JSON.stringify(e, null, 2)); 
            }

        });    

    });

});
